/*Table structure for table `plus_project` */

DROP TABLE IF EXISTS `plus_project`;

CREATE TABLE `plus_project` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '提交用户',
  `title` varchar(64) NOT NULL DEFAULT '' COMMENT '项目名',
  `intro` varchar(128) NOT NULL DEFAULT '' COMMENT '简介',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '阶段(0概念1研发2正式)',
