  `email` varchar(20) NOT NULL DEFAULT '' COMMENT '联系邮箱',
  `sortid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序id',
  `totalfinancing` decimal(20,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '已筹金额',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='项目表';

