/*Table structure for table `plus_session` */

DROP TABLE IF EXISTS `plus_session`;

CREATE TABLE `plus_session` (
  `user_id` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `group_id` int(5) unsigned NOT NULL DEFAULT '0' COMMENT '用户组',
  `username` char(15) NOT NULL DEFAULT '' COMMENT '用户名',
  `email` char(32) NOT NULL DEFAULT '' COMMENT '邮箱',
  `phone` char(11) NOT NULL DEFAULT '' COMMENT '手机',
  `password` char(32) NOT NULL DEFAULT '' COMMENT '密码',
  `expire` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '过期时间',
  `ip` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`user_id`),
  KEY `ip` (`ip`),
  KEY `expireIndex` (`expire`)
) ENGINE=MEMORY DEFAULT CHARSET=utf8 COMMENT='在线用户表';

/*Data for the table `plus_session` */

/*Table structure for table `plus_user` */

DROP TABLE IF EXISTS `plus_user`;

CREATE TABLE `plus_user` (
  `uid` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户uid',
  `username` char(15) NOT NULL DEFAULT '' COMMENT '用户名',
  `email` char(35) NOT NULL DEFAULT '' COMMENT '邮箱',
  `phone` char(11) NOT NULL DEFAULT '' COMMENT '手机',
  `password` char(32) NOT NULL DEFAULT '' COMMENT '密码',
  `salt` char(8) NOT NULL DEFAULT '' COMMENT '密钥',
  PRIMARY KEY (`uid`),
  UNIQUE KEY `usernameIndex` (`username`),
  UNIQUE KEY `emailIndex` (`email`),
  UNIQUE KEY `phoneIndex` (`phone`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='用户登录表';

/*Data for the table `plus_user` */

/*Table structure for table `plus_user_address` */

DROP TABLE IF EXISTS `plus_user_address`;

CREATE TABLE `plus_user_address` (
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户id',
  `name` varchar(20) NOT NULL DEFAULT '' COMMENT '联系人',
  `phone` varchar(20) NOT NULL DEFAULT '' COMMENT '联系人电话',
  `province_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '省region_id',
  `city_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '市region_id',
  `county_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '县region_id',
